import React, { useState } from 'react';
import poster from "../"

const Home = () => {
  const [gifPlayed, setGifPlayed] = useState(false);

  const handleGifLoad = () => {
    if (!gifPlayed) {
      setGifPlayed(true);
    }
  };

  return (
    <div>
      {!gifPlayed ? (
        <img
          src="https://im2.ezgif.com/tmp/ezgif-2-64fb3f08a6.gif"
          alt="GIF"
          onLoad={handleGifLoad}
        />
      ) : (
        <div>
          <img src="poster.png" alt="Poster" />
          <button>Play</button>
        </div>
      )}
    </div>
  );
};

export default Home;
