import React, { useState, useEffect } from "react";
import "./style.css";
import Rock from "./rock.png";
import Paper from "./paper.png";
import Scissor from "./scissors.png";
const Game = () => {
  const choices = ["rock", "paper", "scissor"];
  const [playerChoice, setPlayerChoice] = useState(null);
  const [computerChoice, setComputerChoice] = useState(null);
  const [result, setResult] = useState(null);
  const [showPopup, setShowPopup] = useState(true);
  const [playerName, setPlayerName] = useState("");
  const [instructions, setInstructions] = useState("");

  const playRound = (choice) => {
    const computer = getComputerChoice();
    const roundResult = playerRound(choice, computer);
    setPlayerChoice(choice);
    setComputerChoice(computer);
    setResult(roundResult);
  };

  const getComputerChoice = () => {
    return choices[Math.floor(Math.random() * choices.length)];
  };

  const playerRound = (playerSelection, computer) => {
    let result;
    let computerScore = 0;
    let playerScore = 0;
    const player = playerSelection.toLowerCase();

    if (player === computer) {
      result = "Draw Match";
      computerScore = computerScore;
      playerScore = playerScore;
    } else if (player === "rock" && computer === "paper") {
      result = "You Lose ... 'Paper beats Rock'";
      computerScore += 1;
    } else if (player === "rock" && computer === "scissor") {
      result = "You Win!!! Rock breaks the scissor";
      playerScore += 1;
    } else if (player === "paper" && computer === "rock") {
      result = "Hurray you win!! Paper covers the rock";
      playerScore += 1;
    } else if (player === "paper" && computer === "scissor") {
      result = "Oh no you lose.. Scissor cuts the paper";
      computerScore += 1;
    } else if (player === "scissor" && computer === "rock") {
      result = "You lose .. Rock breaks the scissor";
      computerScore += 1;
    } else if (player === "scissor" && computer === "paper") {
      result = "You win!! Scissor cuts the paper";
      playerScore += 1;
    } else {
      result = "You entered the wrong choice";
    }
    return result;
  };

  const closePopup = () => {
    setShowPopup(false);
  };


  useEffect(() => {
    if (!showPopup) {
      const gameInstructions = "Welcome to Rock Paper Scissors Game! \n";
      const optionInstructions = "Please select one of the options: rock, paper, or scissor";
      setInstructions(gameInstructions + optionInstructions);
  
      // Start the game with the default choice
      playRound(choices[0]);
    }
  }, [showPopup]);

  return (
    <div className="App">
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h1>Welcome to Rock Paper Scissors Game!</h1>
            <p>Please enter your name:</p>
            <input
              type="text"
              value={playerName}
              onChange={(e) => setPlayerName(e.target.value)}
            />
            <button onClick={closePopup}>OK</button>
          </div>
        </div>
      )}
      {!showPopup && (
        <div>
          <h1>Rock Paper Scissors Game</h1>
          <div>
            <div className="container">
              {playerChoice && computerChoice && (
                <div className="game-container">
                  <div className="player-choice">
                    <p>Player's Choice: {playerChoice}</p>
                    {playerChoice === "rock" ? (
                      <img
                        src={Rock}
                        alt="Rock"
                        style={{ transform: "scaleX(-1)" }}
                      />
                    ) : playerChoice === "paper" ? (
                      <img
                        src={Paper}
                        alt="Paper"
                        style={{ transform: "scaleX(-1)" }}
                      />
                    ) : (
                      <img
                        src={Scissor}
                        alt="Scissor"
                        style={{ transform: "scaleX(-1)" }}
                      />
                    )}
                  </div>
                  <div className="computer-choice">
                    <p>Computer's Choice: {computerChoice}</p>
                    {computerChoice === "rock" ? (
                      <img src={Rock} alt="Rock" />
                    ) : computerChoice === "paper" ? (
                      <img src={Paper} alt="Paper" />
                    ) : (
                      <img src={Scissor} alt="Scissor" />
                    )}
                  </div>
                </div>
              )}
              <p className="result">Result: {result}</p>
            </div>

            <div>
              <h1 style={{ paddingLeft: "50px" }}>Select your Choice</h1>
              <div className="choice">
                {choices.map((choice) => (
                  <button key={choice} onClick={() => playRound(choice)}>
                    {choice === "rock" ? (
                      <img src={Rock} className="Rock" alt="Rock" />
                    ) : choice === "paper" ? (
                      <img src={Paper} className="Paper" alt="Paper" />
                    ) : (
                      <img src={Scissor} className="Scissor" alt="Scissor" />
                    )}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Game;
