import React, { useRef, useEffect } from 'react';
import "./Home.css";
import video from "./Video.mp4";
import Poster from "./Poster.png";

const Home = () => {
  const videoRef = useRef(null);

  useEffect(() => {
    const videoElement = videoRef.current;

    const handleVideoEnd = () => {
      videoElement.removeEventListener('ended', handleVideoEnd);
      videoElement.pause();
      videoElement.style.display = 'none';
      document.getElementById('poster').style.display = 'block';
    };

    videoElement.addEventListener('ended', handleVideoEnd);
    videoElement.play();

    return () => {
      videoElement.removeEventListener('ended', handleVideoEnd);
    };
  }, []);

  const handlePlayClick = () => {
    const videoElement = videoRef.current;
    document.getElementById('poster').style.display = 'none';
    videoElement.style.display = 'block';
    videoElement.play();
  };

  return (
    <div style={{ position: 'relative', width: '100%', height: '90vh' }}>
  <video ref={videoRef} width="100%" height="100%" controls>
    <source src={video} type="video/mp4" />
  </video>
  <div id="poster" style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', textAlign: 'center' }}>
    <img src={Poster} alt="Poster" className='home-image'/>
    <button onClick={handlePlayClick} style={{ marginTop: '1rem' }}>Play</button>
  </div>
</div>

  );
};

export default Home;
