      <h1>Rock Paper Scissors Game</h1>
      <div>
        {choices.map((choice) => (
          <button key={choice} onClick={() => playRound(choice)}>
            <img src={choice === "rock" ? rockImage : choice === "paper" ? paperImage : scissorsImage} alt={choice} />
          </button>
        ))}
      </div>
      {result && (
        <div className="result-section">
          <h2>Game Result</h2>
          <p>Player's Choice: {playerChoice}</p>
          <p>Computer's Choice: {computerChoice}</p>
          <p>Result: {result}</p>
        </div>
      )}
    </div>
  );
};

export default Game;
